function calculateLabelPosition(pathData) {
  // console.log(svgPathBbox);
  const bbox = svgPathBbox(pathData);
  const [x1, y1, x2, y2] = bbox;
  return {
    x: (x1 + x2) / 2,
    y: (y1 + y2) / 2,
  };
}

const initRegions = async () => {
  try {
    await connectDB();
    // Smazání všech existujících záznamů
    await Region.deleteMany({});
    console.log("Všechna data byla smazána");

    // Vložení nových záznamů
    for (const region of regions) {
      const labelPosition = calculateLabelPosition(region.pathData);
      console.log(`Vypočítaná pozice pro ${region.name}:`, labelPosition);
      await Region.create({ ...region, labelPosition });
      console.log(`Přidán nový kraj: ${region.name}`);
    }

    console.log("Data byla úspěšně synchronizována");
    process.exit();
  } catch (error) {
    console.error("Chyba při synchronizaci dat:", error);
    process.exit(1);
  }
};

initRegions();

